import React, { Component } from 'react';
import './style.css';
import Counter from './counter';

class Counters extends Component {
    state = { 
        Counters: [
            {id: 1, value: 5},
            {id: 2, value: 9},
            {id: 3, value: 4},
            {id: 4, value: 6}
        ],
        newValue: 0, // لتخزين الرقم المدخل في حقل الأرقام
    };

    handleValueChange = (event) => {
        // تحديث الحالة بناءً على الرقم المدخل في حقل الأرقام
        this.setState({ newValue: parseInt(event.target.value) });
    };

    handleApplyValue = (id) => {
        // تطبيق الرقم المدخل على عنصر معين بناءً على الـ id
        const Counters = [...this.state.Counters];
        const index = Counters.findIndex(c => c.id === id);
        Counters[index] = { ...Counters[index], value: this.state.newValue };
        this.setState({ Counters });
    };

    renderTags() {
        if (this.state.Counters.length === 0) 
            return <p className='ms_error'>This array is empty</p>;

        return (
            <ul className='list_test_one'>
                {this.state.Counters.map(item => 
                    <li key={item.id}>
                        <Counter 
                            id={item.id} 
                            value={item.value} 
                            onApply={() => this.handleApplyValue(item.id)} 
                        />
                    </li>
                )}
            </ul>
        );
    }

    render() { 
        return (
            <React.Fragment>
                <input 
                    type="number" 
                    placeholder="Enter new value" 
                    onChange={this.handleValueChange} 
                />
                <br></br>
                {this.state.Counters.length === 0 && <p className='ms_error'>This array is empty</p>}
                {this.renderTags()}
                <button
                    onClick={() => this.handleApplyValue(this.state.selectedId)}
                    className='btn btn-primary btn-lg mt-3'>
                    Apply New Value
                </button>
            </React.Fragment>
        );
    }
}

export default Counters;
