import React, { Component } from 'react';
import './style.css'

class counter extends Component {
    state = { 
        count: 0
     } 
    render() { 
        return (
            <React.Fragment>
                <span className='num'>{this.formatCount()}</span>
                <button>
                   Increment
                </button>
            </React.Fragment>
        );
    }
    formatCount(){
        return this.state.count=== 0?<h1></h1>:this.state.count
    }
}
 
export default counter;
