import React, { Component } from "react";
import "./style.css";
import Counter from "./counter";
// المصفوفه والتعامل معها في رياكت
class Counters extends Component {
  state = {
    Counters: [
      { id: 1, value: 0 },
      { id: 2, value: 11 },
      { id: 3, value: 22 },
      { id: 4, value: 33 },
      { id: 5, value: 44 },
      { id: 6, value: 55 },
      { id: 7, value: 66 },
      { id: 8, value: 77 },
      { id: 9, value: 88 },
      { id: 10, value: 99 },
    ],
  };

  renderTags() {
    if (this.state.Counters.length === 0)
      return <p className="ms_error">this array empaty</p>;
    return (
      <div className="list_test_one">
        {this.state.Counters.map((item) => (
          <div className="items_list_test_one">
            <Counter
              key={item.id}
              onDelete={this.handelDelete}
              value={item.value}
              id={item.id}
            />

          </div>
        ))}
      </div>
    );
  }
  // fun => When you press that button that contains the erase function, the existing number is erased.
  handelDelete = (counterId) => {
    const counters = this.state.Counters.filter((c) => c.id !== counterId);
    this.setState({ counters });
  };
  render() {
    return (
      <React.Fragment>
        {/* <input type="number" name="" id="" /> */}
        <br></br>
        {this.state.Counters.length === 0 && (
          <p className="ms_error">this array empaty</p>
        )}
        {this.renderTags()}
      </React.Fragment>
    );
  }
}

export default Counters;
